using UnityEngine; using System.Collections;  namespace MFPP.Modules {     [HelpURL("https://ashkoredracson.github.io/MFPP/#pick-up-module")]      [DisallowMultipleComponent]       public class PickUpModule : PlayerModule     {         /// <summary>         /// The maximum pickup distance.         /// </summary>         [Space]         [Tooltip("The maximum pickup distance.")]         public float MaxPickupDistance = 2f;         public float breakDistance = 5f;         public float maxVelocity = 12;          [Tooltip("Pick up button.")]         public string PickUpButton = "Pick Up";         public Transform playerCam;         public GameObject heldObject;         public FootstepAsset originalFootstep;         public FootstepAsset dragFootstep;         public Rigidbody target;          [SerializeField] private LayerMask newLayerMask;         private PlayerCharge pc;         private Camera mainCamera;         private float originalWalkingSpeed = 3;         private IconContainer iconContainer;         private AudioSource audioSource;         private AudioClip pickupClip;         private AudioClip dropClip;         private AudioClip popClip;          void Start ()         {             mainCamera = Camera.main;             pc = GetComponent<PlayerCharge>();             audioSource = GetComponent<AudioSource>();             SoundBox sound = GetComponent<SoundBox>();             pickupClip = sound.pickup;             popClip = sound.pop;             dropClip = sound.deselect;             iconContainer = Toolbox.Instance.GetIconContainer();         }           public override void AfterUpdate()         {             if (Input.GetButtonDown(PickUpButton)) // If pick up button was pressed             {                 if (target) // If we already have a target rigidbody, set it to null, thus dropping/throwing it.                 {PutDown();}                 else                 {PrepareForPickup();}             }              if (target) // If target is not null, move the target in front of the camera at max pickup distance.
            {                 InteractableObject.ObjectType type = target.GetComponent<InteractableObject>().objectType;                 bool check = CheckPlayerStance();                  if (!check){                     target.gameObject.GetComponent<InteractableObject>().HoldPosition();                     //ObjectPositioning(type);
                }                  if (target && Vector3.Distance(this.transform.position, target.transform.position) >= breakDistance)                 {                     PutDown();                 }              }         }          public void PickUp (Rigidbody body) {             target = body; // Set the target             heldObject = target.gameObject;              //have the object do the pickup changes!             target.GetComponent<InteractableObject>().Pickup();             pc.UpdatePredictingSlider();              //play sounds for pickup!             audioSource.clip = pickupClip;             audioSource.Play();          }          public void PutDown () {              //drop the object             target.GetComponent<InteractableObject>().Drop();             heldObject = null; //remove from the list             target = null;             pc.UpdatePredictingSlider();              //plays sound effect             audioSource.clip = dropClip;             audioSource.Play();         }          private void PrepareForPickup()         {             //check if the object is already selected, remove it from list             if (heldObject && heldObject.GetComponent<InteractableObject>().selected)             {                 RaycastManager rm = GetComponent<RaycastManager>();                 rm.RemoveFromList(target.gameObject, true, false);                 rm.selectedObjs.Remove(target.gameObject);                 heldObject.GetComponent<Renderer>().material.SetInt("_IsSelected", 1);                 pc.UpdatePredictingSlider();             }               if (this.gameObject.layer == 15) { newLayerMask.value = 1024; } //layermask value of layer 10 is 1024             else if (this.gameObject.layer == 16) { newLayerMask.value = 2048; } //layermask value of layer 11 is 2048              Ray r = new Ray(mainCamera.transform.position, mainCamera.transform.forward);             RaycastHit hit;             if (Physics.Raycast(r, out hit, MaxPickupDistance, newLayerMask.value)) // Otherwise, if target was null, shoot a ray where we are aiming.             {                 Rigidbody body = hit.collider.attachedRigidbody;                 if  (body && body.CompareTag("Clickable") &&                     ((body.gameObject.layer + 5) == this.gameObject.layer))                         { PickUp(body); }             }         }           public void KillPickup()         {             target = null;             heldObject = null;         }          private bool CheckPlayerStance()         {
            //AVOIDING FLYING ON TOP OF HELD OBJECT GLITCH:
            //checks rotation of player so that it's down (the between the 67 and 91f)
            //checks that the player is actually looking at OBJECT, (colored crosshair)
            if ((playerCam.transform.localEulerAngles.x > 55f) &&                  (playerCam.transform.localEulerAngles.x < 91f) &&                  GetComponent<RaycastManager>().crossHair.color == new Color32(255, 222, 77, 255))             {                 PutDown();                 return true;             }              else return false;         }      } }